/* eslint-disable vue/valid-v-slot */
<template>
    <div class="card-body mt-2">
        <strong>{{race.track.name}} {{formatDate(race.raceDate)}} Race {{race.raceNumber}}</strong><br>
        {{race.conditions.type}}<br>
        {{race.conditions.text}}<br>
        <strong>Purse:</strong> {{race.purse.text}}<br>
        <span v-for="(enhancement, eindex) in race.purse.enhancements" :key="eindex"><strong>{{enhancement.type}}:</strong> {{enhancement.text}}</span><br>
        <strong>Available Money:</strong> {{race.purse.availableMoney}}<br>
        <strong>Weather:</strong> {{race.weather.text}} <strong>Track:</strong> {{race.trackCondition}}<br>
        <strong>Off at:</strong> {{race.postTime}} <strong>Start:</strong> {{race.startComments}} <strong>Timer:</strong> {{race.timer}}
        <b-table
            id="runningLines"
            :items="race.starters"
            :fields="starterFields"
            small
            primary-key="program"
        >
        <template #cell(lastRaced)="row">
            <span v-if="row.item.lastRaced">{{formatDate(row.item.lastRaced.raceDate)}}<sup>{{row.item.lastRaced.raceNumber}}</sup> {{row.item.lastRaced.track.code}}<sup>{{row.item.lastRaced.officialPosition}}</sup></span>
        </template>
         <template #cell(horse.name)="row">
            {{row.item.horse.name}} ({{row.item.jockey.name}})
        </template>       
        </b-table>        
    </div>
</template>

<script>
export default {
    name: 'ChartView',
    components: {

    },
    props : ['race', 'starterFields'],
    data () {
		return {

        }
    },
    methods: {
		formatDate (date) {
			return date[1] + "/" + date[2] + "/" + date[0];
		},	
    }
}
</script>
