import { BIconCircleFill, BIconArrowDown, BIconArrowUp } from 'bootstrap-vue';
import axios from 'axios';
import _ from 'underscore';
export default {
  name: 'PastPerformanceView',
  components: {
    BIconCircleFill,
    BIconArrowDown,
    BIconArrowUp
  },
  props: ['horse', 'race'],
  data() {
    return {
      ppFields: [{
        key: "raceDateString",
        label: "DATE"
      }, {
        key: "daysSinceLastRace",
        label: "LR",
        tdClass: this.highlightDaysSincePP
      }, {
        key: "trackCode",
        label: "TRK"
      }, {
        key: "surface",
        label: "",
        tdClass: this.highlightSimilarSurfDist
      }, {
        key: "dist",
        lable: "DIST",
        tdClass: this.highlightSimilarSurfDist
      }, {
        key: "trackCondition",
        label: "",
        tdClass: this.highlightSimilarSurfDist
      }, {
        key: "fraction1",
        label: "1C"
      }, {
        key: "fraction2",
        label: "2C"
      }, {
        key: "fraction3",
        label: "STR"
      }, {
        key: "finalTime",
        label: "FIN"
      }, {
        key: "raceStrength",
        label: "RS",
        formatter: this.format2Places
      }, {
        key: "raceClassification",
        label: "RACETYPE"
      }, {
        key: "purse",
        tdClass: this.highlightPurseShift
      }, {
        key: "trainerAndClaimFlag",
        label: "Trnr"
      }, {
        key: "e1",
        tdClass: this.highlightPace
      }, {
        key: "e2",
        label: "E2/",
        tdClass: this.highlightPace
      }, {
        key: "paceFigureLate",
        label: "LP",
        tdClass: this.highlightPace
      }, {
        key: "raceShapeFirstCall",
        label: "1c",
        tdClass: this.highlightRaceShape
      }, {
        key: "raceShapeSecondCall",
        label: "2c",
        tdClass: this.highlightRaceShape
      }, {
        key: "brisspeedRating",
        label: "SPD",
        class: "strong",
        tdClass: this.highlightSpeedInRange
      }, {
        key: "speedRating",
        label: "SR-TV"
      }, {
        key: "postPosition",
        label: "PP"
      }, {
        key: "startCallPosition",
        label: "ST"
      }, {
        key: "firstCallPosition",
        label: "1C"
      }, {
        key: "secondCallPosition",
        label: "2C"
      }, {
        key: "stretchPosition",
        label: "STR"
      }, {
        key: "finishPosition",
        label: "FIN",
        tdClass: this.highlightFinish
      }, {
        key: "jockey",
        label: "JOCKEY"
      }, {
        key: "medication",
        label: ""
      }, {
        key: "odds",
        label: "ODDS",
        tdClass: this.highlightBeatenFavorite
      }, {
        key: "finishers",
        label: "Top Finishers"
      }, {
        key: "tripComment",
        label: "Comment"
      }, {
        key: "numberOfEntrants",
        label: ""
      }]
    };
  },
  methods: {
    async toggleIgnored(item) {
      try {
        await axios({
          url: 'toggleIgnored/' + this.race.raceNumber + '/' + this.horse.programNumber + '/' + item.raceDate[0] + '/' + item.raceDate[1] + '/' + item.raceDate[2],
          method: 'GET',
          baseURL: 'http://localhost:8080/jpp/rest/remote/'
        });
        item.ignore = !item.ignore;
      } catch (err) {
        console.log(err.response);
      }
    },
    ppRowClass(item, type) {
      if (!item || type !== 'row') return;
      var c = "";
      if (item.over365Days) c += "diff365";
      if (item.over90Days && !item.over365Days) c += "diff90";
      if (item.ignore) c += " table-dark ignored";
      return c;
    },
    fractionString(decimal) {
      switch (decimal) {
        case 0.13:
          return "hd";
        case 0.25:
          return "&frac14;";
        case 0.5:
          return "&frac12;";
        case 0.75:
          return "&frac34;";
      }
    },
    shortenName(value) {
      return value.split(" ")[0] + value.split(" ")[1].charAt(0);
    },
    format2Places(amount) {
      const formatter = new Intl.NumberFormat('en-US', {
        // These options are needed to round to whole numbers if that's what you want.
        minimumFractionDigits: 2,
        // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
        maximumFractionDigits: 2 // (causes 2500.99 to be printed as $2,501)
      });

      return formatter.format(amount);
    },
    formatInt(amount) {
      const formatter = new Intl.NumberFormat('en-US', {
        // These options are needed to round to whole numbers if that's what you want.
        minimumFractionDigits: 0,
        // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
        maximumFractionDigits: 0 // (causes 2500.99 to be printed as $2,501)
      });

      return formatter.format(amount);
    },
    formatCurrency(amount) {
      const formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
        // These options are needed to round to whole numbers if that's what you want.
        minimumFractionDigits: 0,
        // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
        maximumFractionDigits: 0 // (causes 2500.99 to be printed as $2,501)
      });

      return formatter.format(amount);
    },
    str_pad_left(string, pad, length) {
      return (new Array(length + 1).join(pad) + string).slice(-length);
    },
    highlightDaysSincePP(value, key, item) {
      if (item.daysSinceLastRace > 180) return "gt180";
      if (item.daysSinceLastRace > 90) return "gt90";
      if (item.daysSinceLastRace > 45) return "gt45";
    },
    highlightFinish(value, key, item) {
      if (item.finishPosition == "1") return "greenHighlight";
      if (item.finishPosition == "2" || item.finishPosition == "3") return "lightGreenHighlight";
    },
    highlightBeatenFavorite(value, key, item) {
      if (item.favoriteFlag && item.finishPosition != "1") return "yellowHighlight";
    },
    highlightPurseShift(value, key, item) {
      if (item.purse >= this.race.purse + 20000) return "greenHighlight";
      if (item.purse > this.race.purse) return "lightGreenHighlight";
      if (item.purse == this.race.purse) return "";
      if (item.purse < this.race.purse - 10000) return "yellowHighlight";
      if (item.purse < this.race.purse - 20000) return "lightRedHighlight";
      if (item.purse < this.race.purse - 30000) return "redHighlight";
    },
    highlightSpeedInRange(value, key, item) {
      if (item.brisspeedRating >= this.race.parSpeed) return "greenHighlight";
      if (item.brisspeedRating >= this.race.parSpeed - 3) return "lightGreenHighlight";
    },
    highlightPaceAdvantage(value, key, item) {
      if (_.contains(_.pluck(this.race.advantagedHorses, "programNumber"), item.programNumber)) return "greenHighlight";
    },
    highlightPace(value, key, item) {
      if (item.e1 >= (this.race.furlongs < 8 ? this.race.parPace2F : this.race.parPace4F) - 2 && item.e2 >= (this.race.furlongs < 8 ? this.race.parPace4F : this.race.parPace6F) - 2 && item.paceFigureLate >= this.race.parLatePace - 2) return "greenHighlight";
    },
    highlightRaceShape(value, key, item) {
      if (item.raceShapeFirstCall > 9 && item.raceShapeSecondCall > 9 && this.horse.runStyle == 'E') return 'yellowHighlight';
      if (item.raceShapeFirstCall > 9 && item.raceShapeSecondCall > 9 && (this.horse.runStyle == 'P' || this.horse.runStyle == 'S')) return 'greenHighlight';
      if (item.raceShapeFirstCall < -9 && item.raceShapeSecondCall < -9 && this.horse.runStyle == 'E') return 'greenHighlight';
      if (item.raceShapeFirstCall < -9 && item.raceShapeSecondCall < -9 && (this.horse.runStyle == 'P' || this.horse.runStyle == 'S')) return 'yellowHighlight';
    },
    highlightShift(value) {
      if (value < 0) return "greenHighlight";
      if (value > 0) return "yellowHighlight";
    },
    highlightSimilarSurfDist(value, key, item) {
      if (this.race.surface.indexOf("TURF") > -1 && !this.race.offTheTurfFlag) {
        if (item.surface.indexOf("TURF") > -1 && (this.race.trackCondition == "fm" && item.trackCondition == "fm" || this.race.trackCondition != "fm" && ["tf", "gd", "gf", "hd", "yl", "sf", "hy", "sl"].indexOf(item.trackCondition) > -1) && (this.race.furlongs < 8 && item.furlongs < 8 || this.race.furlongs >= 8 && item.furlongs >= 8)) return "blueHighlight";
      } else {
        if ((item.surface.indexOf("TURF") == -1 || item.offTheTurfFlag) && (this.race.trackCondition == "ft" && item.trackCondition == "ft" || this.race.trackCondition != "ft" && ["gd", "hy", "my", "sl", "sy", "wf"].indexOf(item.trackCondition) > -1) && (this.race.furlongs < 8 && item.furlongs < 8 || this.race.furlongs >= 8 && item.furlongs >= 8)) return "blueHighlight";
      }
    },
    alsoInRace(nameToMatch, row) {
      var names = _.pluck(this.race.horses, "name");
      if (names.indexOf(nameToMatch) > -1 && nameToMatch != row.name) return "alsoInRace";
    }
  }
};
